package days;

import java.util.stream.IntStream;

public class Six {
  private static final String INPUT = "";

  public int getFirstMarkerIndex(int n) {
    for (int i = n; i < INPUT.length(); i++) {
      long count = IntStream.range(i - n, i + 1)
              .map(INPUT::charAt)
              .distinct()
              .count();
      if(count == n+1) {
        return i+1;
      }
    }
    return -1;
  }
}
